import java.time.LocalDate
import java.time.temporal.WeekFields
import java.util.Locale

def flowFile = session.get()
if(!flowFile) return

flowFile = session.write(flowFile, { inputStream, outputStream ->
    def text = inputStream.getText("UTF-8")
    def json = new groovy.json.JsonSlurper().parseText(text)

    json.each { record ->
        def date = LocalDate.parse(record.date)
        def weekNum = date.get(WeekFields.ISO.weekOfWeekBasedYear())
        record.week = String.format("%02d", weekNum)
        record.year = date.get(WeekFields.ISO.weekBasedYear())
    }

    def output = groovy.json.JsonOutput.toJson(json)
    outputStream.write(output.getBytes("UTF-8"))
} as org.apache.nifi.processor.io.StreamCallback)

session.transfer(flowFile, REL_SUCCESS)

