import groovy.json.JsonSlurper
import java.time.LocalDate

def ff = session.get()
if (!ff) return

def text = new String(ff.read().bytes)
def json = new JsonSlurper().parseText(text)

json.currencies.each { currency ->
    (json.startYear..json.endYear).each { year ->
        def out = session.create()
        out = session.putAttribute(out, "currency", currency)
        out = session.putAttribute(out, "start_date", "${year}-01-01")
        out = session.putAttribute(out, "end_date", "${year}-12-31")
        out = session.putAttribute(out, "year", year.toString())
        session.transfer(out, REL_SUCCESS)
    }
}

session.remove(ff)
